var HashTable=function(){var Keys=[];var Values=[];var public={Count:0,Contains:function(k){for(var i=0;i<Keys.length;i++){if(Keys[i]==k||i===k){return true}}return false},KeyIndex:function(k){for(var i=0;i<Keys.length;i++){if(Keys[i]==k||i===k){return i}}return null},ContainsValue:function(oV){oV=(typeof(oV)=='string')?encodeURIComponent(oV):oV;for(var i=0;i<Values.length;i++){if(Values[i]===oV){return true}}return false},Add:function(k,v){if(k!==undefined){if(this.Contains(k)){Values[this.KeyIndex(k)]=encodeURIComponent(v)}else{Keys.push(k.toString());if(typeof(v)=='string'){Values.push(encodeURIComponent(v))}else{Values.push(v)}this.Count++}}},Concat:function(k,v){if(k!==undefined&&this.Contains(k)){var i=this.KeyIndex(k);if(i!=null){if(typeof(Values[i])=='string'){Values[i]+=encodeURIComponent(v)}}}},AddQueryString:function(p){if(p===undefined){p=window.location.toString()}if(p.indexOf('=')==-1&&p.indexOf('%3D')!=-1){p=decodeURIComponent(p)}p=p.replace(/&amp;/g,'&');if(p.indexOf('?')!=-1){p=p.substring(p.indexOf('?')+1,p.length)}if(p!=''){p=p.split('&');for(var i=0;i<p.length;i++){var kP=p[i].split('=');if(kP.length==2){var v=kP[1];if(typeof(v)=='string'){v=decodeURIComponent(v)}this.Add(kP[0],v)}}}},Get:function(k){var oR=null;var i=this.KeyIndex(k);if(i!=null){oR=Values[i];if(typeof(oR)=='string'){oR=decodeURIComponent(Values[i])}}return oR},Clear:function(){Keys=[];Values=[];this.Count=0},Remove:function(k){var i=this.KeyIndex(k);if(i!=null){Keys.splice(i,1);Values.splice(i,1);this.Count--}},ToString:function(d){var sR='';d=(d===undefined)?true:d;for(var i=0;i<Keys.length;i++){var sV=Values[i].toString();if(d){sV=decodeURIComponent(sV)}sR+='&'+Keys[i]+'='+sV}sR=sR.substring(1,sR.length);return sR}};return public};